package week1.day1;

import java.util.Iterator;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 101; i++)
			if (i % 2 == 0) {
				System.out.println(i+" - even");

			} else {
				System.out.println(i+" -odd");

			}
	}

}
